mapper.select:
select topic.topic_id, topic.writer_id, topic.title, topic.content, topic.cre_date, topic.cre_time, topic.upd_date, topic.upd_time from topic where 1=1
and topic.writer_id = #{writerId}
and topic.category = #{category}
and topic.title like '%'||#{keyword}||'%' or topic.content like '%'||#{keyword}||'%' or topic.writer_id like '%'||#{keyword}||'%'
order by cre_date desc, cre_time desc
